SELECT movie_id FROM (SELECT movie_id,info_type_id,info,kind_id FROM imdb.movie_info inner join imdb.title on imdb.movie_info.movie_id = imdb.title.id) AS T where (T.info_type_id = 16 and (T.info like '%2000%' or T.info like '%2001%'or T.info like '%2002%' or T.info like '%2003%' or T.info like '%2004%' or T.info like '%2005%' or T.info like '%2006%'or T.info like '%2007%' or T.info like '%2008%' or T.info like '%2009%' or T.info like '%2010%') and T.info like '%USA%' and T.kind_id = 1);